import { useState } from 'react';
import { useNavigate } from "react-router-dom";

import '../styles/newreservation.css';


// Formular zur Erstellung einer neuen Tischreservierung
function ReservationForm({ setReservation }) {
    // Zustände für die Formularfelder
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [phone, setPhone] = useState('');
    const [reservationTime, setReservationTime] = useState('');
    const navigate = useNavigate();

    // Funktion zum Absenden des Formulars
    const handleSubmit = async (e) => {
        e.preventDefault();

        const newReservation = {
            tableNumber,
            reservation: { name, email, phone, reservationTime }
        };

        try {
            console.log("Sende Reservierung:", newReservation);

            // Anfrage an das Backend zur Erstellung der Reservierung
            const response = await fetch("http://localhost:8080/api/reservations", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                credentials: "include", // JWT-Cookie wird mitgesendet
                body: JSON.stringify(newReservation)
            });

            console.log("Antwort erhalten:", response);

            if (!response.ok) {
                throw new Error("Fehler beim Senden der Reservierung");
            }

            const data = await response.json();
            console.log("Reservierung hinzugefügt:", data);

            // Die neue Reservierung im State speichern (zur Anzeige)
            setReservation([data]);

            // Formular zurücksetzen
            setName("");
            setEmail("");
            setPhone("");
            setReservationTime("");
            setTableNumber("");

        } catch (err) {
            console.error("Fehler beim Hinzufügen der Reservierung:", err);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="reservation-form">
            {/* Eingabe: Name */}
            <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />

            {/* Eingabe: E-Mail-Adresse */}
            <input
                type="email"
                placeholder="E-Mail"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />

            {/* Eingabe: Telefonnummer */}
            <input
                type="tel"
                placeholder="Telefonnummer"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
            />

            {/* Eingabe: Datum und Uhrzeit */}
            <input
                type="datetime-local"
                value={reservationTime}
                onChange={(e) => setReservationTime(e.target.value)}
                required
            />

            {/* Auswahl des Tisches */}
            <select
                id="table"
                name="table"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                required
            >
                <option value="">Wähle einen Tisch...</option>
                <option value="1">Tisch 1 (2 Personen)</option>
                <option value="2">Tisch 2 (3 Personen)</option>
                <option value="3">Tisch 3 (6 Personen)</option>
                <option value="4">Tisch 4 (8 Personen)</option>
                <option value="5">Tisch 5 (2 Personen)</option>
                <option value="6">Tisch 6 (4 Personen)</option>
            </select>

            {/* Button zum Absenden */}
            <button type="submit">Reservieren</button>
        </form>
    );
}

export default ReservationForm;
